mapper.select:

select rownum, bno, title, content, writer, regdate, views
from (
		select @rownum:=@rownum+1 as rownum,b.*
        from (
			select @rownum:=0
		) as tmp, tbl_qna as b
		order by bno asc
) as qnalist
where gdsNum = #{gdsNum}

and rownum>(#{pageNum} - 1)*#{amount} and rownum<=#{pageNum} *#{amount}


select rownum, rno, rating, content, reviewimg, writer
from (
		select @rownum:=@rownum+1 as rownum,b.*
        from (
			select @rownum:=0
		) as tmp, tbl_review as b
		order by rno asc
) as reviewlist
where gdsNum = #{gdsNum}

and rownum>(#{rvpageNum} - 1)*#{rvamount} and rownum<=#{rvpageNum} *#{rvamount}
